import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
class App extends Component {
  constructor(){
    super()//call the constructor function of the component class
    this.state={
      monsters:[
        {name:"Frankensein",
        id:'123q'
      },
        {name:"Dracula",
        id:'as3r'},
        {name:"Zombie",
        id:'101q'},
        {name:"Killer",
        id:'102q'}

      ]
    }
    //locate in class app
  }

render() {
  return (
    <div className="App">
      {
        this.state.monsters.map((monsters)=>{
        return <h1 key={monsters.id}>
          {monsters.name}
        </h1>;})
      }
    </div>
  );


}
}

export default App;
