import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
class App extends Component {
  constructor(){
    super()//call the constructor function of the component class
    this.state={
      monsters:[
        {name:"Frankensein"},
        {name:"Dracula"},
        {name:"Zombie"},

      ]
    }
    //locate in class app
  }
render() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          {this.state.string}
        </p>
        <button onClick={()=>this.setState({string:'hellow'})}>Change Component</button>
      </header>
    </div>
  );
}
}

export default App;
